const isAlpha = str => /^[a-zA-Z]*$/.test(str);

function solution(a,b){
    if(a.length!==b.length){
        return false;
    }
    for(let i=0;i<a.length;i++){
        if(!isAlpha(a[i])||!isAlpha(b[i])){
            return false;
        }
    }
    var A=a.split("").sort().join("");
    var B=b.split("").sort().join("");
    return A===B;    
}