function test(st){
    for(var i=0;i<st.length;i++){
      if(st.charCodeAt(i)<65 ||( st.charCodeAt(i)>90 && st.charCodeAt(i)<97) ||st.charCodeAt(i)>122)
      {
        return false;
       } 
      } return true; 
    }
  function solution(a, b) {
    console.log(a,b);
    res=false; 
    if(test(a) && test(b))
    { 
       if(a.split("").sort().join('')===b.split("").sort().join('')){
         res=true; 
       }   
  } return res;
  }